import React, { useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { StyleSheet, Text, View, SafeAreaView } from 'react-native';
import {
  Container,
  Header,
  Content,
  Form,
  Item,
  Input,
  Label,
  Left,
  Right,
  Body,
  Title,
  Button,
  Icon,
} from 'native-base';
import { NavigationContainer, useNavigation } from '@react-navigation/native';
import FarmerScreen from './FarmerScreen';
import AuthenticatedScreen from '../auth/authenticatedScreen';
import { login, getUser } from '../Action/auth';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { color } from 'react-native-reanimated';

const LoginScreen = ({ navigation: { navigate } }) => {
  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
    role: 'Customer',
    Active: 'true',
  });

  const { email, password, role, Active } = loginData;
  return (
    // <SafeAreaView>
    <Container>
      <Header>
        <Left />
        <Body>
          <Title>Login</Title>
        </Body>
        <Right />
      </Header>
      <Content>
        <Form onSubmit={(event) => signIn(event)}>
          <Item
            floatingLabel
            value={email}
            name='email'
            id='email'
            onChangeText={(event) => onChange(event)}
          >
            <Label>Email</Label>
            <Input />
          </Item>
          <Item
            floatingLabel
            type
            value={password}
            name='password'
            id='password'
            onChangeText={(event) => onChange(event)}
          >
            <Label>Password</Label>
            <Input />
          </Item>
          <Item floatingLabel>
            <Input disabled placeholder='Customer' />
          </Item>
        </Form>
        <Button full iconLeft onPress={() => navigate('MATERIAL_HOME')}>
          <Icon type='MaterialCommunityIcons' name='login' />
          <Text>Login</Text>
        </Button>
        <TouchableOpacity
          style={styles.toRegister}
          onPress={() => navigate('MATERIAL_REGISTER')}
        >
          <Text style={{ color: 'orange' }}>
            Don't have an account...Click Here
          </Text>
        </TouchableOpacity>
      </Content>
    </Container>
    // </SafeAreaView>
  );
};
LoginScreen.prototypes = {
  login: PropTypes.func.isRequired,
  auth: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  auth: state.auth,
});

export default connect(mapStateToProps, { login })(LoginScreen);

const styles = StyleSheet.create({
  toRegister: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingTop: 20,
  },
});
