import React from 'react';
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  Button,
  ImageBackground,
  Platform,
  StatusBar,
  Image,
  TouchableOpacity,
} from 'react-native';

const WelcomeScreen = () => {
  return (
    <ImageBackground
      style={styles.background}
      source={require('../assets/farmerMarket.jpg')}
    >
      <View style={styles.textContainer}>
        <Text style={styles.logoText}>Local Online</Text>
        <Text style={styles.logoText}>Organic goodness from your Computer</Text>
      </View>
      <TouchableOpacity style={styles.loginButton}>
        <View>
          <Text style={styles.text}>Login</Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity style={styles.registerButton}>
        <View>
          <Text style={styles.text}>Register</Text>
        </View>
      </TouchableOpacity>
    </ImageBackground>
  );
};

const styles = StyleSheet.create({
  background: {
    flex: 1,
    justifyContent: 'flex-end',
    alignItems: 'center',
  },
  loginButton: {
    width: '100%',
    height: 70,
    backgroundColor: 'tomato',
  },
  logoText: {
    fontFamily: 'Georgia-BoldItalic',
    fontSize: 30,
    fontWeight: 'bold',
  },

  registerButton: {
    width: '100%',
    height: 70,
    backgroundColor: 'green',
  },
  text: {
    fontFamily: 'Georgia-BoldItalic',
    fontSize: 30,
    fontWeight: 'bold',
    position:"absolute",
    left:"50%"
  },

  textContainer: {
    position: 'absolute',
    top: 70,
    alignItems: 'center',
  },
});

export default WelcomeScreen;
